	animations: {
		run: 'ani_chr_1-forest_run_v03.fbx'
	}
};

annie.userData.animations = {};
annie.userData.play = function ( action ) {

	for ( var k in annie.userData.animations ) {

		var animation = annie.userData.animations[ k ];
		animation.action[ k === action ? 'play' : 'stop' ]();

	}

};
loader.load( path + files.model, modelLoaded );

FRAME.addResource( 'annie', annie );

function modelLoaded ( fbx ) {

	annie.add( fbx );
	annie.userData.model = fbx;
	annie.userData.model.mixer = new THREE.AnimationMixer( fbx );

	fbx.traverse( function ( child ) {

		if ( child.isMesh ) {

			child.material.side = THREE.DoubleSide;

		}

	} );

	for ( var k in files.animations ) {

		var animation = files.animations[ k ];
		loader.load( path + animation, animationLoaded( k ) );

	} 

}

function animationLoaded ( name ) {

	return function ( fbx ) {

		var animation = fbx.animations[ 0 ];
		animation.name += ': ' + name;
		animation.action = annie.userData.model.mixer.clipAction( animation );
		annie.userData.animations[ name ] = animation;

	};

}

