files.forEach(function(file) {
	loader.load(path + file, function(obj) {

		var reference;

		obj.traverse( function ( child ) {

			if ( child.isMesh ) reference = child;

		} );

		for ( var i = 0; i < 5; i ++ ) {

			var clone = reference.clone();
			clone.position.x = Math.random() * 40 - 20;
			clone.position.z = Math.random() * 80 - 40;
			clone.rotation.y = Math.random() * Math.PI * 2;
			clone.userData.startPosition = clone.position.clone();
			group.add( clone );

		}

	});
});

