var clock = new THREE.Clock();

resources.clock = clock;

var renderer = new THREE.WebGLRenderer( { antialias: true, preserveDrawingBuffer: true } );
renderer.autoClear = false;
renderer.setSize( dom.clientWidth, dom.clientHeight );
dom.appendChild( renderer.domElement );

resources.renderer = renderer;

var camera = new THREE.PerspectiveCamera();
camera.aspect = dom.clientWidth / dom.clientHeight;
camera.updateProjectionMatrix();

camera.far = 1000;

var annie = new THREE.Mesh(
	new THREE.CylinderBufferGeometry(0, 2, 6, 16),
	new THREE.MeshBasicMaterial({
		color: 'white'
	})
);

annie.cursor = new THREE.Vector2();
annie.update = function( speed, theta ) {
	var step = speed * clock.getDelta();
	annie.cursor.x += step * Math.sin( theta );
	annie.cursor.y += step * Math.cos( theta );
	return annie;
};
annie.rotation.set(Math.PI / 2, 0, Math.PI);
annie.add( new THREE.Mesh(
	annie.geometry.clone(),
	new THREE.MeshBasicMaterial({
		transparent: true,
	    color: 'white',
	    depthTest: false,
	    depthWrite: false,
	    opacity: 0.33
	})
) );

resources.annie = annie;

camera.position.set( 0, 50, 100 );
camera.lookAt( annie.position );

resources.camera = camera;

