var parameters = {
	speed: new FRAME.Parameters.Float( 'Speed', 50 ),
	amount: new FRAME.Parameters.Integer( 'Amount', 512 )
};

var amount = parameters.amount.value;
var stage = new THREE.Vector2( 512, 1024 );

var scene = new THREE.Scene();
var geometries = [
	new THREE.CylinderBufferGeometry( 0, 0.1, 1, 16, 16 ),
	new THREE.CylinderBufferGeometry( 0.1, 0.1, 1, 16, 16 ),
	new THREE.BoxBufferGeometry( 0.1, 1, 0.1 )
];
var materials = [
	new THREE.MeshBasicMaterial({
		color: 'green',
		transparent: true,
		opacity: 0.33
//		wireframe: true
	})
];

var meshes = [];
for (var i = 0; i < amount; i++) {

	var mesh = new THREE.Mesh(
		geometries[Math.floor(Math.random() * geometries.length)],
		materials[Math.floor(Math.random() * materials.length)]
	);

	mesh.position.x = Math.random() * stage.x - stage.x / 2;
	mesh.position.z = Math.random() * stage.y - stage.y / 2;
	mesh.scale.setScalar(Math.random() * 100 + 1);

	mesh.userData.offset = mesh.position.clone();	

	scene.add(mesh);
	meshes.push(mesh);
}

function start() {
	scene.add( resources.annie );
}

function update( progress ) {

	var density = Math.sin( progress * Math.PI );

	resources.annie.update( parameters.speed.value, 0 );

	for ( var i = 0; i < meshes.length; i++ ) {

		var mesh = meshes[ i ];
		var cursor = resources.annie.cursor;
		var offset = mesh.userData.offset;

		mesh.position.x = ( cursor.x + offset.x ) % ( stage.x / 2 );
		mesh.position.z = ( cursor.y + offset.z ) % ( stage.y / 2 ) - stage.y / 4;

		mesh.scale.y = Math.max( mesh.scale.x * density, 0.1 );
		mesh.position.y = mesh.scale.y / 2;

	}

	resources.renderer.render( scene, resources.camera );

}

function mod(v, l) {
	while (v < 0) {
		v += l;
	}
	return v % l;
}

