function enhance ( stage ) {

	var files = stage.files;
	var length = files.length;
	var loaded = 0;
	var isComplete = false;

	stage.geometries = [];
	stage.onLoad = new Signal();
	stage.ready = function ( func ) {
		if ( isComplete ) {
			func();
			return;
		}
		stage.onLoad.add( func );
	};

	files.complete = function () {

		loaded++;

		if ( !isComplete && loaded >= length ) {
			isComplete = true;
			stage.onLoad.dispatch();
		}


	};

}

function load ( stage, path ) {

	var files = stage.files;
	var url = [ root, 'models', stage.name, path ].join ( '/' );

	loader.load( url, function ( fbx ) {

		extract( stage, fbx );
		files.complete();

	} );

}

function extract ( stage, fbx ) {

	var geometries = stage.geometries;

	fbx.traverse( function( child ) {

		if ( child.isMesh ) {
			geometries.push( child.geometry );
		}

	} );

}

