function update ( progress, dt ) {

	if ( material.uniforms && material.uniforms.time ) {
		material.uniforms.time.value += (dt || 0 ) * 4;
	}

	var animation = annie.userData.currentAnimation;

	if ( controllers.dragging() ) {

		theta.destination -= controllers.deltas.rotation.y;
		step.destination = 0.05 * parameters.step.value;

		if ( animation ) {
			animation.action.timeScale = 0.75;
		}

	} else {

		if ( animation ) {
			animation.action.timeScale = 1.3;
		}
		step.destination = 0.1 * parameters.step.value;

	}

	step.current += ( step.destination - step.current ) * theta.drag;
	theta.current += ( theta.destination - theta.current ) * theta.drag;

	var size = parameters.size.value;
	var amplitude = step.current * ( dt || 0 );
	var forceField = parameters.forceField.value;

	cursor.x += amplitude * Math.cos( theta.current );
	cursor.y += amplitude * Math.sin( theta.current );

	group.rotation.y = theta.current - Math.PI / 2;

	for ( var i = 0; i < elements.length; i++ ) {

		var mesh = elements[ i ];
		var offset = mesh.userData.offset;

		var x = mod( cursor.x + offset.x, 1 ) - 0.5;
		var y = mod( cursor.y + offset.y, 1 ) - 0.5;

		var angle = Math.atan2( - y, - x );
		var buffer = forceField * ( 128 / size );
		var phi = angle + Math.PI;

		mesh.position.x = x + Math.cos( phi ) * buffer;
		mesh.position.z = y + Math.sin( phi ) * buffer;

		mesh.position.x *= - size;
		mesh.position.z *= - size;

	}

}

function mod ( v, l ) {
	while ( v < 0 ) { v += l; }
	return v % l;
}

